import { useEffect, useState } from "react";
import { GetData } from "../../helpers/GetStates";

import "./Select.style.css";

const Select = ({ id, field, value, setData }) => {
  const options = GetData(field.options);
  const [isOpen, setIsOpen] = useState(false);
  const [displayOptions, setDisplayOptions] = useState(options);
  const [search, setSearch] = useState("");

  useEffect(() => {
    if (value) {
      setSearch(options.find((option) => option.id === value).name);
    } else {
      setSearch("");
    }
    setDisplayOptions(
      options.filter((option) => {
        if (option.name) {
          return (
            option.name
              .toLocaleLowerCase()
              .search(search.toLocaleLowerCase()) !== -1
          );
        }
        return options;
      })
    );
  }, [search, options, selected, value]);

  const handleSelect = (e, option) => {
    e.preventDefault();
    setSearch(option.name);
    setData((oldData) => ({ ...oldData, [field.key]: option.id }));
    setIsOpen(false);
  };

  return (
    <div className="select" id={id}>
      <input
        onClick={() => setIsOpen((oldOpen) => !oldOpen)}
        // onFocus={() => setIsOpen(true)}
        // onBlur={() => setIsOpen(false)}
        onChange={(e) => setSearch(e.target.value)}
        value={search}
      ></input>
      {isOpen && (
        <div>
          {displayOptions.map((option) => {
            return (
              <div key={option.id} onClick={(e) => handleSelect(e, option)}>
                {option.name}
              </div>
            );
          })}
        </div>
      )}
    </div>
  );
};

export default Select;
