import { useEffect, useState } from "react";
import { GetData, GetDataById } from "../../helpers/GetStates";

import "./Select.style.css";

const SelectInput = ({
  setDisplayOptions,
  options,
  value,
  setIsOpen,
  field,
}) => {
  const selected = GetDataById(field.options, value);
  const [search, setSearch] = useState(value ? selected.name : "");
  useEffect(() => {
    setDisplayOptions(
      options.filter((option) => {
        if (option.name) {
          return (
            option.name
              .toLocaleLowerCase()
              .search(search.toLocaleLowerCase()) !== -1
          );
        }
        return options;
      })
    );
  }, [search, options]);
  return (
    <input
      onClick={() => setIsOpen((oldOpen) => !oldOpen)}
      // onFocus={() => setIsOpen(true)}
      // onBlur={() => setIsOpen(false)}
      onChange={(e) => setSearch(e.target.value)}
      value={search}
    ></input>
  );
};

const Select = ({ id, field, value, setData }) => {
  const options = GetData(field.options);
  const [isOpen, setIsOpen] = useState(false);
  const [displayOptions, setDisplayOptions] = useState(options);

  useEffect(() => {
    //     if (value) {
    //       setSearch(options.find((option) => option.id === value).name);
    //     } else {
    //       setSearch("");
    //     }
  }, []);

  const handleSelect = (e, option) => {
    e.preventDefault();
    //     setSearch(option.name);
    setData((oldData) => ({ ...oldData, [field.key]: option.id }));
    setIsOpen(false);
  };

  return (
    <div className="select" id={id}>
      <SelectInput
        setDisplayOptions={setDisplayOptions}
        value={value}
        options={options}
        setIsOpen={setIsOpen}
        field={field}
      />
      {isOpen && (
        <div>
          {displayOptions.map((option) => {
            return (
              <div key={option.id} onClick={(e) => handleSelect(e, option)}>
                {option.name}
              </div>
            );
          })}
        </div>
      )}
    </div>
  );
};

export default Select;
